$(document).ready(function(){$(".percentage").easyPieChart({animate:1000,size:135,barColor:"#FF2D55"});$(".percentage-light").easyPieChart({barColor:function(a){a/=100;return"rgb("+Math.round(255*(1-a))+", "+Math.round(255*a)+", 0)"},trackColor:"#666",scaleColor:false,lineCap:"butt",lineWidth:15,animate:1000});$(".update-easy-pie-chart").click(function(){$(".easy-pie-chart .percentage").each(function(){var a=Math.floor(100*Math.random());$(this).data("easyPieChart").update(a);$("span",this).text(a)})});$(".updateEasyPieChart").on("click",function(a){a.preventDefault();$(".percentage, .percentage-light").each(function(){var b=Math.round(100*Math.random());$(this).data("easyPieChart").update(b);$("span",this).text(b)})})});