$(document).ready(function(){var b=location.protocol+"//"+location.host+(location.port?":"+location.port:"");var a='<div class="modal fade" id="myModal" role="dialog"><div class="modal-dialog" style="color:#fff; text-align:center; padding-top:300px;"><i class="fa fa-2x fa-spinner fa-spin" aria-hidden="true"></li></div></div>';$("#check-all").change(function(){if(this.checked){$(".checkbox").each(function(){this.checked=true})}else{$(".checkbox").each(function(){this.checked=false})}});$(".btn-send-mail").click(function(d){d.preventDefault();var c=$(this).data("email");jQuery.ajax({method:"get",url:b+"/dragongate/admin/subscribe/send_mail",data:{email:c},beforeSend:function(){$("#encypted_ppbtn").html(a)},success:function(f){$(".fade").css("display","none");var e=JSON.parse(f).isExitsts;if(e==true){alert("Gửi mail thành công")}else{alert("Gửi mail thất bại")}}})});$("#sendAll").click(function(d){d.preventDefault();var c=[];$(".checkbox").each(function(){if($(this).prop("checked")==true){c.push(this.id)}});if(c!=""){jQuery.ajax({method:"get",url:b+"/dragongate/admin/subscribe/send_mail_all",data:{ids:c},beforeSend:function(){$("#encypted_ppbtn_all").css("display","block")},success:function(f){$("#encypted_ppbtn_all").css("display","none");var e=JSON.parse(f);if(e==null){alert("Gửi thành công đến tất cả email được đánh dấu")}else{alert("Các email không gửi thành công: "+e)}}})}else{alert("Chưa có email được chọn")}return false})});